function documentReady(fn) {
    if (document.readyState != 'loading') {
        fn();
    } else {
        document.addEventListener('DOMContentLoaded', fn);
    }
}

// Get navigator language
var langArray = navigator.language.split('-');
var language = (langArray[1]) ? langArray[1].toLowerCase() : navigator.language.toLowerCase();
var isPlLanguage = (language === 'pl');
var isCSLanguage = (language === 'cs');

console.log(language);

function updateText() {
    let description = 'Complete the puzzle to confirm.';
    if (isPlLanguage) {
        description = 'Przesuń układankę by dokonać potwierdzenia.'
    }
    if (isCSLanguage) {
        description = 'Pro potvrzení dokončete puzzle.'
    }
    document.getElementsByClassName('captcha__human__submit-description')[0].innerHTML = description;
}

documentReady(function () {
    var descriptionField = document.getElementsByClassName('captcha__human__submit-description');
    if (descriptionField.length === 0) {
        var descText = 'We have noticed unusual activity of your network or browser. We want to make sure that you are a human.';
        if (isPlLanguage) {
            descText = 'Zauważyliśmy nietypowe działanie Twojej sieci lub przeglądarki. Potrzebujemy upewnić się, że jesteś człowiekiem.';
