        var requestIdElement = document.getElementById('show-human-requestId');
        var elementRequest = document.createElement("div");
        elementRequest.classList.add("human-request-id");
        elementRequest.innerHTML = '<p>' + requestIdElement.innerHTML + '</p>';
        document.getElementById('captcha-container').appendChild(elementRequest);
        document.getElementById('show-human-requestId').remove();
    }

    if (document.getElementById('captcha-loader')) {
        var e = document.getElementById('captcha-loader');
        var d = document.createElement('div');
        d.classList.add("m-spinner");
        d.setAttribute("id", "captcha-loader");
        d.style.display = 'none';
        e.parentNode.replaceChild(d, e);
    }
    // Intercept click on support link
    document.getElementById('captcha-container').addEventListener('keyup', function (event) {
        if (event.code === "Enter" && document.activeElement === document.getElementById('show-human-auth')) {
            showForm('captcha__robot__contact_support');
        }
    });
    document.getElementById('show-human-auth').addEventListener('click', function () {
        showForm('captcha__robot__contact_support');
    });
})

