/** 
 * This function will load and trigger the one trust cookie banner. This is done by including
 * the one trust scripts into the DOM. A data-domain-script data-attribute will determine which
 * version is loaded from the assets. 
 */
;(function(){  

  var script = document.createElement("script");
  script.setAttribute("src", "https://cdn.cookielaw.org/scripttemplates/otSDKStub.js");
  script.setAttribute("text", "text/javascript");
  script.setAttribute("charset", "UTF-8");
  // below is the environment id from one trust ui. change it in order to display different visuals
  script.setAttribute("data-domain-script", _satellite.getVar("Config | OneTrust Environment"));
  script.setAttribute("data-document-language", "true");
  script.setAttribute("data-dLayer-ignore", "true");
  //script.setAttribute("data-dLayer-name", "OTDataLayer");
  document.head.appendChild(script);

}());

/** 
 * This function adds a cookie settings button to the logistics footer
 * Footer list is not available at script execution. Therefore we loop
 * until available
 */
;(function(){
 var to = setInterval(function(){
    var footer_list = document.querySelector("#wcag-footer nav ul");
    if(footer_list){
      var li = document.createElement("li");
      li.classList.add("l-grid--w-50pc-s");
      var button = document.createElement("button");
      button.setAttribute("id", "onetrust-pc-btn-handler");
      button.setAttribute("data-document-language", "true");
      button.textContent = "Cookie Settings";
      button.classList.add("link");
      button.classList.add("ot-sdk-show-settings");
      li.appendChild(button);
      footer_list.appendChild(li);
      clearInterval(to);
    }
 }, 200);
}());

