var darkmode = false;

var sdkBridge = new naver_corp_da.SdkBridge({
    viewableImpressed: function () {
        var util = naver_corp_da.Util ? new naver_corp_da.Util() : typeof NBP_CORP !== 'undefined' ? new NBP_CORP.Nimp() : null;

        if (util) {
            util.log("https://siape.veta.naver.com/fxview?eu=EU10043570&calp=-&oj=9gPJTdEI4gGLUeR674XDMNSRRmyvzamcht2So1LWEJc31G9G6b7lCyzK37izVaCYCLPE0Vl%2B7RzihtCRZSbft5ZDzJeAsQ1a&ac=8823753&src=6643223&evtcd=V900&x_ti=1507&tb=&oid=&sid1=&sid2=&rk=F7906wDUpShi3OeOmd-zyQ&eltts=1C36n4mEI9huxgxBgpCA2w%3D%3D&brs=Y&&eid=V900&t=i");
        }
    },
    hostMetaChanged: function(hostMeta) {
        var publicDA = window.document.getElementById("publicDA");

        darkmode = hostMeta.theme === 'dark';

        if (darkmode) {
            publicDA.classList.add('type_dark');
        } else {
            publicDA.classList.remove('type_dark');
        }
    }
});

function initAd() {
    checkOS();
    initAltForImage();
    sdkBridge.init();
}

function checkOS() {
    var container = document.querySelector('.main_public');

    if (!container || !AgentDetect.IS_WIN) {
        return;
    }

    container.classList.add('type_win');
}

function initAltForImage() {
  // DA Alternative - Create Access Layer
  var accessLayer = document.createElement('div');
  accessLayer.setAttribute('id', 'da_access');
  accessLayer.style.cssText = 'position:absolute;top:50%;left:50%;width:154px;height:58px;margin:-29px 0 0 -77px;background:url(https://ssl.pstatic.net/melona/libs/res/www/common/info/da_access.png)';
  accessLayer.style.display = "none";
  var oAdDiv = document.getElementById("ac_banner_a");
  oAdDiv.appendChild(accessLayer);

  // DA Alternative - Focus Action
  var altLink = document.getElementById("ac_banner_a");
  var bMousedown = false;

  altLink.onmousedown = function() {
    bMousedown = true;
  };

  altLink.onfocus = function() {
    if (!bMousedown) {
      accessLayer.style.display = "block";
    }
  };

  altLink.onblur = function() {
    accessLayer.style.display = "none";
  };
}
