	function getLanguage() {
        let frRegex = /\/content\/(.*)\/fr\//;
        let pagePath = "/content/bnc/en/accueil/particuliers";

        if(frRegex.test(pagePath)){
            return "fr";
        }
        else{ //default is English
            return "en";
        }

    }


    /*
    * Sets the script parameters to be the same as the default parameters configured by the site
    */
	function setSiteConfig(){


        scriptUrl = "https://dam.bnc.ca/content/dam/tools/cmp/notice.js";
        configDidomi = ("true" === "true");
        activateDidomi = ("true" === "true");
        applyDidomiToChildren = ("true" === "true");
        showDidomi = ("true" === "true");
        siteToken = "bFXc6fFy";
		lang = getLanguage();
    }


	/*
    * Updates the script parameters if a configuration was set on a page or on a parent page
    */
	function setChildParentConfig(){

                    configDidomi = ("true" === "true");
                    activateDidomi = ("true" === "true");
                    applyDidomiToChildren = ("true" === "true");
                    showDidomi = ("true" === "true");
                    childToken = "null";
                    if(childToken === "null"){
                        configDidomi = false;
                    }else{
                        configDidomi = true;
                        foundParentConfig = true;
                    }


    }


	/*
    * Sets the notice token based on the page hierarchy
    */
 	function setToken(){
        if(configDidomi){
            //child on
            if(foundParentConfig  && applyDidomiToChildren && activateDidomi){
                bannerToken = childToken;
            }
            //parent on
            if(!foundParentConfig  && activateDidomi){
                bannerToken = childToken;
            }
            //parent on, child on with default
            if(foundParentConfig && !applyDidomiToChildren){
                bannerToken = siteToken;
            }
		} else{ // default
            bannerToken = siteToken;
        }
    }


	/*
    * Loads Didomi script with correct parameters
    */
	function loadJS(FILE_URL, async = true, language, token, enableDidomi) {
        let scriptEle = document.createElement("script");

        scriptEle.setAttribute("src", FILE_URL);
        scriptEle.setAttribute("type", "text/javascript");
        scriptEle.setAttribute("async", async);
        scriptEle.setAttribute("language", language);
        scriptEle.setAttribute("token", token);
        scriptEle.setAttribute("enableDidomi", enableDidomi);

        document.head.appendChild(scriptEle);

        // error event
        scriptEle.addEventListener("error", (ev) => {
        	console.log("Error on loading file", ev);
        });
    }


	/*
    * main function
    */
    function initializeDidomi(){

		// set default parameters
        setSiteConfig();

        // if didomi is active at site level
        if(configDidomi && applyDidomiToChildren && activateDidomi){

            //reset variables for child/parent configs
            configDidomi = false;

            setChildParentConfig();
            setToken();

            loadJS(scriptUrl, true, lang, bannerToken, showDidomi);
        }
		// else didomi should not appear on the page
    }


// PARAMETERS
let lang = "";
let bannerToken = "";
let siteToken = "";
let configDidomi = false;
let applyDidomiToChildren = false;
let activateDidomi = false;
let showDidomi = false;
let foundParentConfig = false;
let childToken = "";
let scriptUrl = "";

initializeDidomi();

