        $(document).ready(function() {

            $("#verify").on('click', function() {
               var playid = $("#playid").val();
               if(playid == "") {
                   $('#errorId').show();
                   $('#playid').css('border','1px solid red');
                   $('#playid').val('');
               } else {
               	   $('#errorId').fadeOut();
                    $.ajax({
                    url: "system/trueeid.php",
                    type: "POST",
                    data: 'playid='+playid,
                    beforeSend: function() {
                    $(".titleId").html('Please wait');
                    $('#playid').fadeOut();
                    $('.waitId').fadeIn();
                    $('#verify').attr('disabled','true');
                    $('#verify').css('opacity','0.5');
                    },
                    success: function(status) {
                    var stat = $('#status').val(status);
                    var isi_stat = stat.val();
                    $("#btn_oke").show();
                    $("#btnload").hide();
                    if (isi_stat == "Wrong_UserId") {
                    	$('#verify').removeAttr('disabled');
	                    $('#verify').css('opacity','1');
                       $('.waitId').hide();
                       $(".titleId").html('Please input PlayerID');
                       $(".errorId").hide();
                       $("#playid").fadeIn();
                       $('#errorId').show();
                   	   $('#playid').css('border','1px solid #fff');
                       $('#errorId').html('invalid player id(2002-2002-0)‎');
                    }else if(isi_stat == "Token Error"){
                    	$('#verify').removeAttr('disabled');
	                    $('#verify').css('opacity','1');
                    	$('.waitId').hide();
                       $(".titleId").html('Please input PlayerID');
                       $(".errorId").hide();
                       $("#playid").fadeIn();
                       $('#errorId').show();
                   	   $('#playid').css('border','1px solid #fff');
                       $('#errorId').html('System error(2002-2002-0)‎');
                    }else{
                        $('#isinick1').val(status);
                        $('#isinick2').val(status);
                        $('#isiplayid1').val(playid);
                        $('#isiplayid2').val(playid);
                        $('.playidVerif').hide();
                        $('.isinama').fadeIn();
                        $('#isinama').html(status);
                        $('#isinama').fadeIn('slow');
                    }
                    }
                    });
               }
            });

        });

