if ('fonts' in document) {
	let font = new FontFace('Frutiger VR', 'url(./style/vr021___-webfont.woff2) format(\'woff2\')');
	let fontBold = new FontFace('Frutiger VR', 'url(./style/vr051___-webfont.woff2) format(\'woff2\')', {
		weight: '700'
	});
	let loadedFonts = Promise.all([
		font.load(),
		fontBold.load()
	]).then((result) => {
		result.forEach(font => document.fonts.add(font));
		document.documentElement.classList.add('wf-loaded');
	}
	).catch((error) => {
		throw new Error(`Error caught: ${error}`);
	});
}
