$(function(){
    var icon = "<i class='fa fa-times-circle'></i>&nbsp;&nbsp;";
    $('#form').validate({
        errorElement : 'span',
        debug: true,//只验证不提交表单
        //layer ajax提交表单
        submitHandler:function(){
            $.ajax({
                url:loginUrl,
                type:'POST',
                data:$('#form').serialize(),
                dataType:'json',
                success:function(data){
                    if(data.status == 1){
                        layer.closeAll('loading');
                        layer.msg(data.mess, {icon: 1,time: 1000},function(){
                            cl();
                        });
                    }else{
                        layer.closeAll('loading');
                        $('#cap').attr('src',"/captcha.html?"+Math.random());
                        layer.msg(data.mess, {icon: 2,time: 2000});
                    }
                },
                error:function(){
                    layer.closeAll('loading');
                    layer.msg('操作失败或您没有权限，请重试', {icon: 2,time: 2000});
                }
            });
            return false;
        },//这是关键的语句，配置这个参数后表单不会自动提交，验证通过之后会去调用的方法

        rules:{
            username:{required:true},
            password:{required:true},
        },

        messages:{
            username:{required:icon+'必填'},
            password:{required:icon+'必填'},
        }
    });
});
function cl(){
    location.href=url;
}
